INSERT INTO productos (id, nombre, slug, precio, usuario_id, requiere_edad, calificacion, stock, categoria_id, descripcion, img_portada, creado_en) VALUES
	('1', 'Majestic Mountain Graphic T-Shirt', 'majestic-mountain-graphic-t-shirt', '44', '1', '0', '4.5', '100', '1', 'Elevate your wardrobe with this stylish black t-shirt featuring a striking monochrome mountain range graphic. Perfect for those who love the outdoors or want to add a touch of nature-inspired design to their look, this tee is crafted from soft, breathable fabric ensuring all-day comfort. Ideal for casual outings or as a unique gift, this t-shirt is a versatile addition to any collection.', 'https://i.imgur.com/QkIa5tT.jpeg', '2024-03-20 19:20:10'),
	('2', 'Classic Red Pullover Hoodie', 'classic-red-pullover-hoodie', '10', '5', '0', '4.5', '80', '1', 'Elevate your casual wardrobe with our Classic Red Pullover Hoodie. Crafted with a soft cotton blend for ultimate comfort, this vibrant red hoodie features a kangaroo pocket, adjustable drawstring hood, and ribbed cuffs for a snug fit. The timeless design ensures easy pairing with jeans or joggers for a relaxed yet stylish look, making it a versatile addition to your everyday attire.', 'https://i.imgur.com/1twoaDy.jpeg', '2025-05-11T13:06:26.000Z'),
	('3', 'Classic Heather Gray Hoodie', 'classic-heather-gray-hoodie', '69', '1', '0', '4.5', '80', '1', 'Stay cozy and stylish with our Classic Heather Gray Hoodie. Crafted from soft, durable fabric, it features a kangaroo pocket, adjustable drawstring hood, and ribbed cuffs. Perfect for a casual day out or a relaxing evening in, this hoodie is a versatile addition to any wardrobe.', 'https://i.imgur.com/wbIMMme.jpeg', '2025-05-11T13:06:26.000Z'),
	('4', 'Classic Grey Hooded Sweatshirt', 'classic-grey-hooded-sweatshirt', '90', '3', '0', '4.5', '110', '1', 'Elevate your casual wear with our Classic Grey Hooded Sweatshirt. Made from a soft cotton blend, this hoodie features a front kangaroo pocket, an adjustable drawstring hood, and ribbed cuffs for a snug fit. Perfect for those chilly evenings or lazy weekends, it pairs effortlessly with your favorite jeans or joggers.', 'https://i.imgur.com/R2PN9Wq.jpeg', '2025-05-11T13:06:26.000Z'),
	('5', 'Classic Black Hooded Sweatshirt', 'classic-black-hooded-sweatshirt', '79', '5', '0', '4.5', '110', '1', 'Elevate your casual wardrobe with our Classic Black Hooded Sweatshirt. Made from high-quality, soft fabric that ensures comfort and durability, this hoodie features a spacious kangaroo pocket and an adjustable drawstring hood. Its versatile design makes it perfect for a relaxed day at home or a casual outing.', 'https://i.imgur.com/cSytoSD.jpeg', '2025-05-11T13:06:26.000Z'),
	('6', 'Classic Comfort Fit Joggers', 'classic-comfort-fit-joggers', '25', '5', '0', '4.5', '90', '1', 'Discover the perfect blend of style and comfort with our Classic Comfort Fit Joggers. These versatile black joggers feature a soft elastic waistband with an adjustable drawstring, two side pockets, and ribbed ankle cuffs for a secure fit. Made from a lightweight and durable fabric, they are ideal for both active days and relaxed lounging.', 'https://i.imgur.com/ZKGofuB.jpeg', '2025-05-11T13:06:26.000Z'),
	('7', 'Classic Comfort Drawstring Joggers', 'classic-comfort-drawstring-joggers', '79', '5', '0', '4.5', '70', '1', 'Experience the perfect blend of comfort and style with our Classic Comfort Drawstring Joggers. Designed for a relaxed fit, these joggers feature a soft, stretchable fabric, convenient side pockets, and an adjustable drawstring waist with elegant gold-tipped detailing. Ideal for lounging or running errands, these pants will quickly become your go-to for effortless, casual wear.', 'https://i.imgur.com/mp3rUty.jpeg', '2025-05-11T13:06:26.000Z'),
	('8', 'Classic Red Jogger Sweatpants', 'classic-red-jogger-sweatpants', '98', '5', '0', '4.5', '70', '1', 'Experience ultimate comfort with our red jogger sweatpants, perfect for both workout sessions and lounging around the house. Made with soft, durable fabric, these joggers feature a snug waistband, adjustable drawstring, and practical side pockets for functionality. Their tapered design and elastic cuffs offer a modern fit that keeps you looking stylish on the go.', 'https://i.imgur.com/9LFjwpI.jpeg', '2025-05-11T13:06:26.000Z'),
	('9', 'Classic Navy Blue Baseball Cap', 'classic-navy-blue-baseball-cap', '61', '5', '0', '4.5', '70', '1', 'Step out in style with this sleek navy blue baseball cap. Crafted from durable material, it features a smooth, structured design and an adjustable strap for the perfect fit. Protect your eyes from the sun and complement your casual looks with this versatile and timeless accessory.', 'https://i.imgur.com/R3iobJA.jpeg', '2025-05-11T13:06:26.000Z'),
	('10', 'Classic Blue Baseball Cap', 'classic-blue-baseball-cap', '86', '3', '0', '4.5', '70', '1', 'Top off your casual look with our Classic Blue Baseball Cap, made from high-quality materials for lasting comfort. Featuring a timeless six-panel design with a pre-curved visor, this adjustable cap offers both style and practicality for everyday wear.', 'https://i.imgur.com/wXuQ7bm.jpeg', '2025-05-11T13:06:26.000Z'),
	('11', 'Classic Red Baseball Cap', 'classic-red-baseball-cap', '35', '2', '0', '4.5', '70', '1', 'Elevate your casual wardrobe with this timeless red baseball cap. Crafted from durable fabric, it features a comfortable fit with an adjustable strap at the back, ensuring one size fits all. Perfect for sunny days or adding a sporty touch to your outfit.', 'https://i.imgur.com/cBuLvBi.jpeg', '2025-05-11T13:06:26.000Z'),
	('12', 'Classic Black Baseball Cap', 'classic-black-baseball-cap', '58', '2', '0', '4.5', '70', '1', 'Elevate your casual wear with this timeless black baseball cap. Made with high-quality, breathable fabric, it features an adjustable strap for the perfect fit. Whether you’re out for a jog or just running errands, this cap adds a touch of style to any outfit.', 'https://i.imgur.com/KeqG6r4.jpeg', '2025-05-11T13:06:26.000Z'),
	('13', 'Classic Olive Chino Shorts', 'classic-olive-chino-shorts', '84', '2', '0', '4.5', '70', '1', 'Elevate your casual wardrobe with these classic olive chino shorts. Designed for comfort and versatility, they feature a smooth waistband, practical pockets, and a tailored fit that makes them perfect for both relaxed weekends and smart-casual occasions. The durable fabric ensures they hold up throughout your daily activities while maintaining a stylish look.', 'https://i.imgur.com/YIq57b6.jpeg', '2025-05-11T13:06:26.000Z'),
	('14', 'Classic High-Waisted Athletic Shorts', 'classic-high-waisted-athletic-shorts', '43', '2', '0', '4.5', '70', '1', 'Stay comfortable and stylish with our Classic High-Waisted Athletic Shorts. Designed for optimal movement and versatility, these shorts are a must-have for your workout wardrobe. Featuring a figure-flattering high waist, breathable fabric, and a secure fit that ensures they stay in place during any activity, these shorts are perfect for the gym, running, or even just casual wear.', 'https://i.imgur.com/NLn4e7S.jpeg', '2025-05-11T13:06:26.000Z'),
	('15', 'Classic White Crew Neck T-Shirt', 'classic-white-crew-neck-t-shirt', '39', '2', '0', '4.5', '70', '1', 'Elevate your basics with this versatile white crew neck tee. Made from a soft, breathable cotton blend, it offers both comfort and durability. Its sleek, timeless design ensures it pairs well with virtually any outfit. Ideal for layering or wearing on its own, this t-shirt is a must-have staple for every wardrobe.', 'https://i.imgur.com/axsyGpD.jpeg', '2025-05-11T13:06:26.000Z'),
	('16', 'Classic White Tee - Timeless Style and Comfort', 'classic-white-tee-timeless-style-and-comfort', '73', '3', '0', '4.5', '80', '1', 'Elevate your everyday wardrobe with our Classic White Tee. Crafted from premium soft cotton material, this versatile t-shirt combines comfort with durability, perfect for daily wear. Featuring a relaxed, unisex fit that flatters every body type, it\'s a staple piece for any casual ensemble. Easy to care for and machine washable, this white tee retains its shape and softness wash after wash. Pair it with your favorite jeans or layer it under a jacket for a smart look.', 'https://i.imgur.com/Y54Bt8J.jpeg', '2025-05-11T13:06:26.000Z'),
	('17', 'Classic Black T-Shirt', 'classic-black-t-shirt', '35', '3', '0', '4.5', '70', '1', 'Elevate your everyday style with our Classic Black T-Shirt. This staple piece is crafted from soft, breathable cotton for all-day comfort. Its versatile design features a classic crew neck and short sleeves, making it perfect for layering or wearing on its own. Durable and easy to care for, it\'s sure to become a favorite in your wardrobe.', 'https://i.imgur.com/9DqEOV5.jpeg', '2025-05-11T13:06:26.000Z'),
	('18', 'Sleek White & Orange Wireless Gaming Controller', 'sleek-white-orange-wireless-gaming-controller', '69', '4', '0', '4.5', '70', '2', 'Elevate your gaming experience with this state-of-the-art wireless controller, featuring a crisp white base with vibrant orange accents. Designed for precision play, the ergonomic shape and responsive buttons provide maximum comfort and control for endless hours of gameplay. Compatible with multiple gaming platforms, this controller is a must-have for any serious gamer looking to enhance their setup.', 'https://i.imgur.com/ZANVnHE.jpeg', '2025-05-11T13:06:26.000Z'),
	('19', 'Sleek Wireless Headphone & Inked Earbud Set', 'sleek-wireless-headphone-inked-earbud-set', '44', '4', '0', '4.5', '70', '2', 'Experience the fusion of style and sound with this sophisticated audio set featuring a pair of sleek, white wireless headphones offering crystal-clear sound quality and over-ear comfort. The set also includes a set of durable earbuds, perfect for an on-the-go lifestyle. Elevate your music enjoyment with this versatile duo, designed to cater to all your listening needs.', 'https://i.imgur.com/yVeIeDa.jpeg', '2025-05-11T13:06:26.000Z'),
	('20', 'Sleek Comfort-Fit Over-Ear Headphones', 'sleek-comfort-fit-over-ear-headphones', '28', '4', '0', '4.5', '70', '2', 'Experience superior sound quality with our Sleek Comfort-Fit Over-Ear Headphones, designed for prolonged use with cushioned ear cups and an adjustable, padded headband. Ideal for immersive listening, whether you\'re at home, in the office, or on the move. Their durable construction and timeless design provide both aesthetically pleasing looks and long-lasting performance.', 'https://i.imgur.com/SolkFEB.jpeg', '2025-05-11T13:06:26.000Z'),
	('21', 'Efficient 2-Slice Toaster', 'efficient-2-slice-toaster', '48', '4', '0', '4.5', '70', '2', 'Enhance your morning routine with our sleek 2-slice toaster, featuring adjustable browning controls and a removable crumb tray for easy cleaning. This compact and stylish appliance is perfect for any kitchen, ensuring your toast is always golden brown and delicious.', 'https://i.imgur.com/keVCVIa.jpeg', '2025-05-11T13:06:26.000Z'),
	('22', 'Sleek Wireless Computer Mouse', 'sleek-wireless-computer-mouse', '10', '4', '0', '4.5', '70', '2', 'Experience smooth and precise navigation with this modern wireless mouse, featuring a glossy finish and a comfortable ergonomic design. Its responsive tracking and easy-to-use interface make it the perfect accessory for any desktop or laptop setup. The stylish blue hue adds a splash of color to your workspace, while its compact size ensures it fits neatly in your bag for on-the-go productivity.', 'https://i.imgur.com/w3Y8NwQ.jpeg', '2025-05-11T13:06:26.000Z'),
	('23', 'Sleek Modern Laptop with Ambient Lighting', 'sleek-modern-laptop-with-ambient-lighting', '43', '4', '0', '4.5', '70', '2', 'Experience next-level computing with our ultra-slim laptop, featuring a stunning display illuminated by ambient lighting. This high-performance machine is perfect for both work and play, delivering powerful processing in a sleek, portable design. The vibrant colors add a touch of personality to your tech collection, making it as stylish as it is functional.', 'https://i.imgur.com/OKn1KFI.jpeg', '2025-05-11T13:06:26.000Z'),
	('24', 'Sleek Modern Laptop for Professionals', 'sleek-modern-laptop-for-professionals', '97', '4', '0', '4.5', '70', '2', 'Experience cutting-edge technology and elegant design with our latest laptop model. Perfect for professionals on-the-go, this high-performance laptop boasts a powerful processor, ample storage, and a long-lasting battery life, all encased in a lightweight, slim frame for ultimate portability. Shop now to elevate your work and play.', 'https://i.imgur.com/ItHcq7o.jpeg', '2025-05-11T13:06:26.000Z'),
	('25', 'Stylish Red & Silver Over-Ear Headphones', 'stylish-red-silver-over-ear-headphones', '39', '4', '0', '4.5', '70', '2', 'Immerse yourself in superior sound quality with these sleek red and silver over-ear headphones. Designed for comfort and style, the headphones feature cushioned ear cups, an adjustable padded headband, and a detachable red cable for easy storage and portability. Perfect for music lovers and audiophiles who value both appearance and audio fidelity.', 'https://i.imgur.com/YaSqa06.jpeg', '2025-05-11T13:06:26.000Z'),
	('26', 'Sleek Mirror Finish Phone Case', 'sleek-mirror-finish-phone-case', '27', '4', '0', '4.5', '70', '2', 'Enhance your smartphone\'s look with this ultra-sleek mirror finish phone case. Designed to offer style with protection, the case features a reflective surface that adds a touch of elegance while keeping your device safe from scratches and impacts. Perfect for those who love a minimalist and modern aesthetic.', 'https://i.imgur.com/yb9UQKL.jpeg', '2025-05-11T13:06:26.000Z'),
	('27', 'Sleek Smartwatch with Vibrant Display', 'sleek-smartwatch-with-vibrant-display', '16', '4', '0', '4.5', '70', '2', 'Experience modern timekeeping with our high-tech smartwatch, featuring a vivid touch screen display, customizable watch faces, and a comfortable blue silicone strap. This smartwatch keeps you connected with notifications and fitness tracking while showcasing exceptional style and versatility.', 'https://i.imgur.com/LGk9Jn2.jpeg', '2025-05-11T13:06:26.000Z'),
	('28', 'Sleek Modern Leather Sofa', 'sleek-modern-leather-sofa', '53', '4', '0', '4.5', '70', '3', 'Enhance the elegance of your living space with our Sleek Modern Leather Sofa. Designed with a minimalist aesthetic, it features clean lines and a luxurious leather finish. The robust metal legs provide stability and support, while the plush cushions ensure comfort. Perfect for contemporary homes or office waiting areas, this sofa is a statement piece that combines style with practicality.', 'https://i.imgur.com/Qphac99.jpeg', '2025-05-11T13:06:26.000Z'),
	('30', 'Elegant Golden-Base Stone Top Dining Table', 'elegant-golden-base-stone-top-dining-table', '66', '4', '0', '4.5', '70', '3', 'Elevate your dining space with this luxurious table, featuring a sturdy golden metal base with an intricate rod design that provides both stability and chic elegance. The smooth stone top in a sleek round shape offers a robust surface for your dining pleasure. Perfect for both everyday meals and special occasions, this table easily complements any modern or glam decor.', 'https://i.imgur.com/NWIJKUj.jpeg', '2025-05-11T13:06:26.000Z'),
	('31', 'Modern Elegance Teal Armchair', 'modern-elegance-teal-armchair', '25', '1', '0', '4.5', '70', '3', 'Elevate your living space with this beautifully crafted armchair, featuring a sleek wooden frame that complements its vibrant teal upholstery. Ideal for adding a pop of color and contemporary style to any room, this chair provides both superb comfort and sophisticated design. Perfect for reading, relaxing, or creating a cozy conversation nook.', 'https://i.imgur.com/6wkyyIN.jpeg', '2025-05-11T13:06:26.000Z'),
	('32', 'Elegant Solid Wood Dining Table', 'elegant-solid-wood-dining-table', '67', '1', '0', '4.5', '70', '3', 'Enhance your dining space with this sleek, contemporary dining table, crafted from high-quality solid wood with a warm finish. Its sturdy construction and minimalist design make it a perfect addition for any home looking for a touch of elegance. Accommodates up to six guests comfortably and includes a striking fruit bowl centerpiece. The overhead lighting is not included.', 'https://i.imgur.com/4lTaHfF.jpeg', '2025-05-11T13:06:26.000Z'),
	('33', 'Modern Minimalist Workstation Setup', 'modern-minimalist-workstation-setup', '49', '1', '0', '4.5', '70', '3', 'Elevate your home office with our Modern Minimalist Workstation Setup, featuring a sleek wooden desk topped with an elegant computer, stylish adjustable wooden desk lamp, and complimentary accessories for a clean, productive workspace. This setup is perfect for professionals seeking a contemporary look that combines functionality with design.', '', '2025-05-11T13:06:26.000Z'),
	('34', 'Modern Ergonomic Office Chair', 'modern-ergonomic-office-chair', '71', '1', '0', '4.5', '70', '3', 'Elevate your office space with this sleek and comfortable Modern Ergonomic Office Chair. Designed to provide optimal support throughout the workday, it features an adjustable height mechanism, smooth-rolling casters for easy mobility, and a cushioned seat for extended comfort. The clean lines and minimalist white design make it a versatile addition to any contemporary workspace.', 'https://i.imgur.com/3dU0m72.jpeg', '2025-05-11T13:06:26.000Z'),
	('35', 'Futuristic Holographic Soccer Cleats', 'futuristic-holographic-soccer-cleats', '39', '1', '0', '4.5', '75', '4', 'Step onto the field and stand out from the crowd with these eye-catching holographic soccer cleats. Designed for the modern player, these cleats feature a sleek silhouette, lightweight construction for maximum agility, and durable studs for optimal traction. The shimmering holographic finish reflects a rainbow of colors as you move, ensuring that you\'ll be noticed for both your skills and style. Perfect for the fashion-forward athlete who wants to make a statement.', 'https://i.imgur.com/qNOjJje.jpeg', '2025-05-11T13:06:26.000Z'),
	('36', 'Rainbow Glitter High Heels', 'rainbow-glitter-high-heels', '39', '1', '0', '4.5', '75', '4', 'Step into the spotlight with these eye-catching rainbow glitter high heels. Designed to dazzle, each shoe boasts a kaleidoscope of shimmering colors that catch and reflect light with every step. Perfect for special occasions or a night out, these stunners are sure to turn heads and elevate any ensemble.', 'https://i.imgur.com/62gGzeF.jpeg', '2025-05-11T13:06:26.000Z'),
	('37', 'Chic Summer Denim Espadrille Sandals', 'chic-summer-denim-espadrille-sandals', '33', '1', '0', '4.5', '75', '4', 'Step into summer with style in our denim espadrille sandals. Featuring a braided jute sole for a classic touch and adjustable denim straps for a snug fit, these sandals offer both comfort and a fashionable edge. The easy slip-on design ensures convenience for beach days or casual outings.', 'https://i.imgur.com/9qrmE1b.jpeg', '2025-05-11T13:06:26.000Z'),
	('38', 'Vibrant Runners: Bold Orange & Blue Sneakers', 'vibrant-runners-bold-orange-blue-sneakers', '27', '1', '0', '4.5', '75', '4', 'Step into style with these eye-catching sneakers featuring a striking combination of orange and blue hues. Designed for both comfort and fashion, these shoes come with flexible soles and cushioned insoles, perfect for active individuals who don\'t compromise on style. The reflective silver accents add a touch of modernity, making them a standout accessory for your workout or casual wear.', 'https://i.imgur.com/hKcMNJs.jpeg', '2025-05-11T13:06:26.000Z'),
	('39', 'Vibrant Pink Classic Sneakers', 'vibrant-pink-classic-sneakers', '84', '1', '0', '4.5', '75', '4', 'Step into style with our Vibrant Pink Classic Sneakers! These eye-catching shoes feature a bold pink hue with iconic white detailing, offering a sleek, timeless design. Constructed with durable materials and a comfortable fit, they are perfect for those seeking a pop of color in their everyday footwear. Grab a pair today and add some vibrancy to your step!', 'https://i.imgur.com/mcW42Gi.jpeg', '2025-05-11T13:06:26.000Z'),
	('40', 'Futuristic Silver and Gold High-Top Sneaker', 'futuristic-silver-and-gold-high-top-sneaker', '68', '1', '0', '4.5', '75', '4', 'Step into the future with this eye-catching high-top sneaker, designed for those who dare to stand out. The sneaker features a sleek silver body with striking gold accents, offering a modern twist on classic footwear. Its high-top design provides support and style, making it the perfect addition to any avant-garde fashion collection. Grab a pair today and elevate your shoe game!', 'https://i.imgur.com/npLfCGq.jpeg', '2025-05-11T13:06:26.000Z'),
	('41', 'Futuristic Chic High-Heel Boots', 'futuristic-chic-high-heel-boots', '36', '1', '0', '4.5', '75', '4', 'Elevate your style with our cutting-edge high-heel boots that blend bold design with avant-garde aesthetics. These boots feature a unique color-block heel, a sleek silhouette, and a versatile light grey finish that pairs easily with any cutting-edge outfit. Crafted for the fashion-forward individual, these boots are sure to make a statement.', 'https://i.imgur.com/HqYqLnW.jpeg', '2025-05-11T13:06:26.000Z'),
	('42', 'Elegant Patent Leather Peep-Toe Pumps with Gold-Tone Heel', 'elegant-patent-leather-peep-toe-pumps', '53', '1', '0', '4.5', '75', '4', 'Step into sophistication with these chic peep-toe pumps, showcasing a lustrous patent leather finish and an eye-catching gold-tone block heel. The ornate buckle detail adds a touch of glamour, perfect for elevating your evening attire or complementing a polished daytime look.', 'https://i.imgur.com/AzAY4Ed.jpeg', '2025-05-11T13:06:26.000Z'),
	('43', 'Elegant Purple Leather Loafers', 'elegant-purple-leather-loafers', '17', '1', '0', '4.5', '75', '4', 'Step into sophistication with our Elegant Purple Leather Loafers, perfect for making a bold statement. Crafted from high-quality leather with a vibrant purple finish, these shoes feature a classic loafer silhouette that\'s been updated with a contemporary twist. The comfortable slip-on design and durable soles ensure both style and functionality for the modern man.', 'https://i.imgur.com/Au8J9sX.jpeg', '2025-05-11T13:06:26.000Z'),
	('44', 'Classic Blue Suede Casual Shoes', 'classic-blue-suede-casual-shoes', '39', '1', '0', '4.5', '75', '4', 'Step into comfort with our Classic Blue Suede Casual Shoes, perfect for everyday wear. These shoes feature a stylish blue suede upper, durable rubber soles for superior traction, and classic lace-up fronts for a snug fit. The sleek design pairs well with both jeans and chinos, making them a versatile addition to any wardrobe.', 'https://i.imgur.com/sC0ztOB.jpeg', '2025-05-11T13:06:26.000Z'),
	('45', 'Sleek Futuristic Electric Bicycle', 'sleek-futuristic-electric-bicycle', '22', '5', '0', '4.5', '75', '12', 'This modern electric bicycle combines style and efficiency with its unique design and top-notch performance features. Equipped with a durable frame, enhanced battery life, and integrated tech capabilities, it\'s perfect for the eco-conscious commuter looking to navigate the city with ease.', 'https://i.imgur.com/BG8J0Fj.jpg', '2025-05-11T13:06:26.000Z'),
	('46', 'Sleek All-Terrain Go-Kart', 'sleek-all-terrain-go-kart', '37', '5', '0', '4.5', '75', '12', 'Experience the thrill of outdoor adventures with our Sleek All-Terrain Go-Kart, featuring a durable frame, comfortable racing seat, and robust, large-tread tires perfect for handling a variety of terrains. Designed for fun-seekers of all ages, this go-kart is an ideal choice for backyard racing or exploring local trails.', 'https://i.imgur.com/Ex5x3IU.jpg', '2025-05-11T13:06:26.000Z'),
	('47', 'Radiant Citrus Eau de Parfum', 'radiant-citrus-eau-de-parfum', '73', '4', '0', '4', '75', '12', 'Indulge in the essence of summer with this vibrant citrus-scented Eau de Parfum. Encased in a sleek glass bottle with a bold orange cap, this fragrance embodies freshness and elegance. Perfect for daily wear, it\'s an olfactory delight that leaves a lasting, zesty impression.', 'https://i.imgur.com/xPDwUb3.jpg', '2025-05-11T13:06:26.000Z'),
	('48', 'Sleek Olive Green Hardshell Carry-On Luggage', 'sleek-olive-green-hardshell-carry-on-luggage', '48', '5', '0', '4.5', '75', '12', 'Travel in style with our durable hardshell carry-on, perfect for weekend getaways and business trips. This sleek olive green suitcase features smooth gliding wheels for easy airport navigation, a sturdy telescopic handle, and a secure zippered compartment to keep your belongings safe. Its compact size meets most airline overhead bin requirements, ensuring a hassle-free flying experience.', 'https://i.imgur.com/jVfoZnP.jpg', '2025-05-11T13:06:26.000Z'),
	('49', 'Chic Transparent Fashion Handbag', 'chic-transparent-fashion-handbag', '61', '5', '0', '4.5', '75', '12', 'Elevate your style with our Chic Transparent Fashion Handbag, perfect for showcasing your essentials with a clear, modern edge. This trendy accessory features durable acrylic construction, luxe gold-tone hardware, and an elegant chain strap. Its compact size ensures you can carry your day-to-day items with ease and sophistication.', 'https://i.imgur.com/Lqaqz59.jpg', '2025-05-11T13:06:26.000Z'),
	('50', 'Trendy Pink-Tinted Sunglasses', 'trendy-pink-tinted-sunglasses', '38', '5', '0', '4.5', '75', '12', 'Step up your style game with these fashionable black-framed, pink-tinted sunglasses. Perfect for making a statement while protecting your eyes from the glare. Their bold color and contemporary design make these shades a must-have accessory for any trendsetter looking to add a pop of color to their ensemble.', 'https://i.imgur.com/0qQBkxX.jpg', '2025-05-11T13:06:26.000Z'),
	('51', 'Elegant Glass Tumbler Set', 'elegant-glass-tumbler-set', '50', '5', '0', '4.5', '75', '12', 'Enhance your drinkware collection with our sophisticated set of glass tumblers, perfect for serving your favorite beverages. This versatile set includes both clear and subtly tinted glasses, lending a modern touch to any table setting. Crafted with quality materials, these durable tumblers are designed to withstand daily use while maintaining their elegant appeal.', 'https://i.imgur.com/TF0pXdL.jpg', '2025-05-11T13:06:26.000Z');
